import { MenuList } from "@mui/material";
import React, { useState, useEffect } from "react";


export default function Favorites() {

  const [myList, setMyList] = useState([]);


  useEffect(()=>{
		for (var i = 0; i < localStorage.length; i++) {
      var x = localStorage.key(i);
      
      try{
        var localStorageInfo = JSON.parse(localStorage.getItem(x))
        //console.log(localStorage.getItem(x));
        const listOfLocalStorage = {"idx" : i, "date" : localStorageInfo[2], "event" : localStorageInfo[0], "category" : localStorageInfo[1], "venue" : localStorageInfo[3], "localStorageId" : x}
        //console.log(listOfLocalStorage)
        setMyList(myList => [...myList, listOfLocalStorage]);
      }
      catch{

      }
      
    }

	}, [])

  
  
  return (
    <div>
        <ul>
        {myList.map(item => (
          <li key={item.idx}>
            {item.idx}{item.date}
          </li>
        ))}
      </ul>

      {myList!==0 && ( <div className="container">
      
        <table className="table table-striped table-dark table-bordered">
        <thead>
          <tr>
            <th>#</th>
            <th>Date</th>
            <th>Event</th>
            <th>Category</th>
            <th>Venue</th>
            <th>Favorite</th>
          </tr>
        </thead>
        <tbody>
          {myList.map((row) => (
            <tr key={row.idx} onClick={() => handleRowClick(row)}>
              <td>{row.date}</td>
              <td>{row.event}</td>
              <td>{row.category}</td>
              <td>{row.venue}</td>
              <td><button id={row.idx}></button></td>
              
            </tr>
          ))}
        </tbody>
      </table>
      </div>
      )}
        
    </div>
  );
}
